import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";



export default function ActivateNumber() {


    const [counts, setCounts] = useState([]);
    useEffect(() => {
        getCounts();
    }, []);

    function getCounts() {
        axios.get('http://localhost:8080/redomat/react-multi-page-website/api/counts/').then(function(response) {
            console.log(response.data);
            setCounts(response.data);
        });
        setTimeout(() => getCounts(), 2000);
    }


    function activateNumber(){
        axios.post(`http://localhost:8080/redomat/react-multi-page-website/api/counts/activate`).then(function(response) {
            console.log(response.data);
            setCounts(response.data);
        });
    }


    /*const deleteUser = (id) => {
        axios.delete(`http://localhost:8888/api/user/${id}/delete`).then(function(response){
            console.log(response.data);
            getUsers();
        });
    }*/
    return (
        <div>
            <h1>Aktivirajte sledećeg kupca</h1>
            <h2>Trenutni broj</h2>
            <h2>{counts.current_number}</h2>
            <button onClick={() => activateNumber()}>Sledeći kupac</button>

            <h2>{counts.msg}</h2>
        </div>
    )
}
