import axios from "axios";
import { useEffect, useState } from "react";
import {  Link } from "react-router-dom";

import io from "socket.io-client";

const socket = io("hws://localhost:3000", {
  transports: ["websocket", "polling"]
});

export default function GetNumber() {

    useEffect(() => {

        socket.on("counts", counts => {
            getCounts();
        });

      }, []);

    const [counts, setCounts] = useState([]);
    useEffect(() => {
        getCounts();
    }, []);


    function getCounts() {
        axios.get('http://localhost:8080/redomat/react-multi-page-website/api/counts/').then(function(response) {
            console.log(response.data);
            setCounts(response.data);
        });
    }

    function reserveNumber(){
        axios.put(`http://localhost:8080/redomat/react-multi-page-website/api/counts/get`).then(function(response) {
            console.log(response.data);
            setCounts(response.data);
        });
    }

    /*const deleteUser = (id) => {
        axios.delete(`http://localhost:8888/api/user/${id}/delete`).then(function(response){
            console.log(response.data);
            getUsers();
        });
    }*/
    return (
        <div>
            <h1>Uzmite broj</h1>
            <h2>Trenutni broj:</h2>
            <h2>{counts.current_number}</h2>

            <button onClick={() => reserveNumber()}>Uzmite broj</button>

            <h2>Rezervisanih brojeva:</h2>
            <h2>{counts.reserved_number}</h2>
        </div>
    )
}
