//stranica za uzimanje broja od strane kupca

import axios from "axios";
import { useEffect, useState } from "react";
import {  Link } from "react-router-dom";


export default function GetNumber() {

    function setPrint () {
        this.contentWindow.__container__ = this;
        this.contentWindow.onbeforeunload = closePrint;
        this.contentWindow.onafterprint = closePrint;
        this.contentWindow.focus(); // Required for IE
        this.contentWindow.print();
        }

        function closePrint () {
        document.body.removeChild(this.__container__);
        }

        function printPage (sURL) {
        var oHiddFrame = document.createElement("iframe");
        oHiddFrame.onload = setPrint;
        oHiddFrame.style.visibility = "hidden";
        oHiddFrame.style.position = "fixed";
        oHiddFrame.style.right = "0";
        oHiddFrame.style.bottom = "0";
        oHiddFrame.src = sURL;
        document.body.appendChild(oHiddFrame);
        }

    const [counts, setCounts] = useState([]);
    useEffect(() => {
        getCounts();
    }, []);


    function getCounts() {//dobijamo prikaz broja koji je trenutno na redu tzv aktivni broj
        axios.get('http://10.0.9.243/nadja/api/').then(function(response) {
            console.log(response.data);
            setCounts(response.data);
        });
        setTimeout(() => getCounts(), 2000);
    }



    function reserveNumber(){ //rezervisemo broj tj to je funkcija za dugme koje korisnici pritisnu i dobiju broj za redomat, http://localhost/react-multi-page-website/api/counts/get
        axios.put(`http://10.0.9.243/nadja/api/`).then(function(response) {
            console.log(response.data);
            setCounts(response.data);
            var url="http://10.0.9.243/nadja/printdoc.php?num="+response.data.reserved_number;
            printPage(url);

        });
        document.getElementById("clickarea").style.pointerEvents="none";
        setTimeout(() => {document.getElementById("clickarea").style.pointerEvents="auto";}, 5000);
    }

    /*const deleteUser = (id) => {
        axios.delete(`http://localhost:8888/api/user/${id}/delete`).then(function(response){
            console.log(response.data);
            getUsers();
        });
    }*/
    return (


<div class ="container">

<div class="item" id="borderinclude"><h1></h1><img src="http://10.0.9.243/nadja/logo.png" alt="slika logo"/><h1>Trenutni aktivni broj je:</h1><h3>{counts.current_number}</h3>
            </div>
<div class="item"  id="clickarea" onClick={() => reserveNumber()} >
<h1 id="click">Kliknite za broj</h1>
</div>

</div>

    )
}
