import {BrowserRouter, Routes, Route, Link} from 'react-router-dom';
import './App.css';
import CreateUser from './components/CreateUser';
import EditUser from './components/EditUser';
import ListNumber from './components/ListNumber';
import GetNumber from './components/GetNumber';
import ActivateNumber from './components/ActivateNumber';

//import { w3cwebsocket as WebSocket } from "websocket";
import React, { useState, useCallback, useEffect } from 'react';
import useWebSocket, { ReadyState } from 'react-use-websocket';


function App() {

  //Public API that will echo messages sent to it back to the client
  const [socketUrl, setSocketUrl] = useState('ws://localhost:3000');
  const [messageHistory, setMessageHistory] = useState([]);

  const {
    sendMessage,
    lastMessage,
    readyState,
  } = useWebSocket(socketUrl);

  useEffect(() => {
    if (lastMessage !== null) {
      setMessageHistory(prev => prev.concat(lastMessage));
    }
  }, [lastMessage, setMessageHistory]);

  const handleClickChangeSocketUrl = useCallback(() =>
    setSocketUrl('ws://localhost:3000', 'echo-protocol'), []);

  const handleClickSendMessage = useCallback(() =>
    sendMessage('Hello'), []);

  const connectionStatus = {
    [ReadyState.CONNECTING]: 'Connecting',
    [ReadyState.OPEN]: 'Open',
    [ReadyState.CLOSING]: 'Closing',
    [ReadyState.CLOSED]: 'Closed',
    [ReadyState.UNINSTANTIATED]: 'Uninstantiated',
  }[readyState];

  /*window.WebSocket = window.WebSocket || window.MozWebSocket;
    var connection = new WebSocket('ws://127.0.0.1:8080/redomat/react-multi-page-website/api/index.php');
    console.log(connection);

      connection.onopen = function () {
          // connection is opened and ready to use
          alert('connection Jej');
        };

        connection.onerror = function (error) {
          // an error occurred when sending/receiving data
          alert('Greska');
        };

        connection.onmessage = function (message) {
          alert('Jej');

        };*/

  return (
    <div className="App">
      <BrowserRouter>
        <nav>
          <ul>
            <li>
              <Link to="/">Trenutni broj</Link>
            </li>
            <li>
              <Link to="counts/get">Uzmite broj</Link>
            </li>
            <li>
              <Link to="counts/activate">Aktiviraj broj</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route index element={<ListNumber />} />
          <Route path="counts/get" element={<GetNumber />} />
          <Route path="counts/activate" element={<ActivateNumber />} />
          <Route path="counts/create" element={<CreateUser />} />
          <Route path="counts/:id/edit" element={<EditUser />} />
        </Routes>
      </BrowserRouter>
    </div>

  );
}

export default App;
