import {BrowserRouter, Routes, Route, Link} from 'react-router-dom';
import './App.css';
import CreateUser from './components/CreateUser';
import EditUser from './components/EditUser';
import ListNumber from './components/ListNumber';
import GetNumber from './components/GetNumber';
import ActivateNumber from './components/ActivateNumber';

//import { w3cwebsocket as WebSocket } from "websocket";
//import React, { useState, useCallback, useEffect } from 'react';
//import useWebSocket, { ReadyState } from 'react-use-websocket';

import React, { useState, useEffect } from "react";

const URL = 'ws://172.20.4.18:8080';


function App() {

  	const [ws, setWs] = useState(new WebSocket(URL));

  	useEffect(() => {
	    ws.onopen = () => {
	      console.log('WebSocket Connected');
	    }

	    return () => {
	      ws.onclose = () => {
	        console.log('WebSocket Disconnected');
	        setWs(new WebSocket(URL));
	      }
	    }
  	}, [ws.onmessage, ws.onopen, ws.onclose]);

  return (
    <div className="App">
      <BrowserRouter>
        <nav>
          <ul>
            <li>
              <Link to="/">Trenutni broj</Link>
            </li>
            <li>
              <Link to="counts/get">Uzmite broj</Link>
            </li>
            <li>
              <Link to="counts/activate">Aktiviraj broj</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route index element={<ListNumber />} />
          <Route path="counts/get" element={<GetNumber />} />
          <Route path="counts/activate" element={<ActivateNumber />} />
          <Route path="counts/create" element={<CreateUser />} />
          <Route path="counts/:id/edit" element={<EditUser />} />
        </Routes>
      </BrowserRouter>
    </div>

  );
}

export default App;
