//mesarkina stranica

import axios from "axios"; //axios sluzi za povezivanje js sa krajnjom tackom 
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";



export default function ActivateNumber() {


    const [counts, setCounts] = useState([]);
    useEffect(() => {
        getCounts();
    }, []);

    //var printNumber = false;

    function getCounts() { //dobijamo broj koji je trenutno na redu
        // D-izmena: promenjena je putanja sa lokalne na putanju fajla na serveru
        axios.get('http://10.0.9.243/nadja/api/').then(function(response) { //get metoda koja pretvara link u response i na taj nacin pretvara promenjivu funkcionalnu za dalje koriscenje u js
            console.log(response.data);
            setCounts(response.data);
        });
        setTimeout(() => getCounts(), 2000); //nakon 2 sekunde refresh-ujemo funkciju za ispisivanje vrednosti brojaca i samim tim ako je dodat neki novi kupac ili usluzen broj se smanjuje i prikazuje
    }


    function activateNumber(){ 

        //aktiviramo broj tj mesarka klikce dugme da preuzme broj koji je sledeci na redu i samim tim se getcounts refreshovanjem menja
        // D-izmena: promenjena je putanja sa lokalne na putanju fajla na serveru
        axios.post(`http://10.0.9.243/nadja/api/`).then(function(response) { // axios.post metoda gde je prvi parametar link ka necemu sto zelimo posle da prosledimo nazad kao response, u sustini je to veza izmedju baze, php-a i js
            console.log(response.data); //console.log ispisuje poruku u konzoli u ovom slucaju ispisace response.data(odgovor) koji smo dobili pomocu axios biblioteke koja sve povezuje php sa js (link od gore)
            setCounts(response.data); //pokrece funkciju setCounts sa parametrima response.data, tj. setcounts je brojac   
        });
        document.getElementById("clickarea").style.pointerEvents="none";
        setTimeout(() => {document.getElementById("clickarea").style.pointerEvents="auto";}, 4000);
        //setTimeout(() => {printNumber=true;}, 5000);

    }


    /*const deleteUser = (id) => {
        axios.delete(`http://localhost:8888/api/user/${id}/delete`).then(function(response){
            console.log(response.data);
            getUsers();
        });
    }*/

    // D-izmena: update putanje za logo i dodavanje dugmeta (potrebno stilizovati)
    return (


        <div class ="container">

        <div class="item" id="borderinclude"><h1></h1><img src="http://10.0.9.243/nadja/logo.png" alt="slika logo"/><h1>Trenutni aktivni broj je:</h1><h3>{counts.current_number}</h3>
                    </div>
        <div class="item" id="clickarea" onClick={() => activateNumber()} > 
        <h1 id="click">SledeÄ‡i kupac  </h1><h2>{counts.msg}</h2>
        </div>

        </div>

    )
}
