import {  Link } from "react-router-dom";


export default function GetNumber() {

      //functions for ticket printer
      function setPrint () {
        this.contentWindow.__container__ = this;
        this.contentWindow.onbeforeunload = closePrint;
        this.contentWindow.onafterprint = closePrint;
        this.contentWindow.focus(); // Required for IE
        this.contentWindow.print();
      }

      function closePrint () {
        document.body.removeChild(this.__container__);
      }

      function printPage (sURL) {
        var oHiddFrame = document.createElement("iframe");
        oHiddFrame.onload = setPrint;
        oHiddFrame.style.visibility = "hidden";
        oHiddFrame.style.position = "fixed";
        oHiddFrame.style.right = "0";
        oHiddFrame.style.bottom = "0";
        oHiddFrame.src = sURL;
        document.body.appendChild(oHiddFrame);
      }

    const [counts, setCounts] = useState([]);
    useEffect(() => {
        getCounts();
    }, []);

    //var printNumber = false;

    //var ip = window.location.host;

    /*
    //made for server version, after build
    function getCounts() {//get current active number
        axios.get('http://'+ip+'/counter-app/api/').then(function(response) {
            console.log(response.data);
            setCounts(response.data);
        });
        setTimeout(() => getCounts(), 2000);
    }*/


    function reserveNumber(){ //customer gets it's number in the waiting line
            axios.put('http://localhost:8080/counter-app/api/').then(function(response) {
            console.log(response.data);
            setCounts(response.data);
            var url='http://localhost:8080/counter-app/printdoc.php?num='+response.data.reserved_number;
            printPage(url);
            });
            document.getElementById("clickarea").style.pointerEvents="none";
            document.getElementById("clickarea").innerHTML="<h1 id='click'>Please take your number</br>Thank you!</h1>";
            setTimeout(() => {document.getElementById("clickarea").style.pointerEvents="auto";document.getElementById("clickarea").innerHTML="<h1 id='click'>Click for number</h1>";}, 4000);
    }

