import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import { w3cwebsocket as W3CWebSocket } from "websocket";


export default function ActivateNumber() {

    window.WebSocket = window.WebSocket || window.MozWebSocket;

    var connection = new WebSocket('ws://localhost:8080/', 'echo-protocol');

      connection.onopen = function () {
          // connection is opened and ready to use
          alert('connection Open');
        };

        connection.onerror = function (error) {
          // an error occurred when sending/receiving data
          alert('Error');
        };

        connection.onmessage = function (message) {
          alert('Message');

        };

    const [counts, setCounts] = useState([]);
    useEffect(() => {
        getCounts();
    }, []);

    function getCounts() {
        axios.get('http://localhost:8080/redomat/react-multi-page-website/api/counts/').then(function(response) {
            console.log(response.data);
            setCounts(response.data);
        });
    }


    function activateNumber(){
        axios.post(`http://localhost:8080/redomat/react-multi-page-website/api/counts/activate`).then(function(response) {
            console.log(response.data);
            setCounts(response.data);
        });
    }


    /*const deleteUser = (id) => {
        axios.delete(`http://localhost:8888/api/user/${id}/delete`).then(function(response){
            console.log(response.data);
            getUsers();
        });
    }*/
    return (
        <div>
            <h1>Aktivirajte sledećeg kupca</h1>
            <h2>Trenutni broj</h2>
            <h2>{counts.current_number}</h2>
            <button onClick={() => activateNumber()}>Sledeći kupac</button>

            <h2>{counts.msg}</h2>
        </div>
    )
}
