import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";

export default function GetNumber() {

    const [counts, setCounts] = useState([]);
    useEffect(() => {
        getCounts();
    }, []);

    const navigate = useNavigate();

    function getCounts() {
        axios.get('http://localhost:8080/redomat/react-multi-page-website/api/counts/').then(function(response) {
            console.log(response.data);
            setCounts(response.data);
        });
    }

    const reserveNumber = (event) => {
        event.preventDefault();

        axios.put(`http://localhost:8080/redomat/react-multi-page-website/api/counts/${counts.reserved_number}/get`).then(function(response){
            console.log(response.data);
            navigate('/get');
        });

    }

    /*const deleteUser = (id) => {
        axios.delete(`http://localhost:8888/api/user/${id}/delete`).then(function(response){
            console.log(response.data);
            getUsers();
        });
    }*/
    return (
        <div>
            <h1>Uzmite broj</h1>
            <h2>{counts.reserved_number}</h2>

            <button onclick={reserveNumber()}>Uzmite broj</button>
        </div>
    )
}
